import React, { useState } from "react";
import Brochure from "./Brochure";
import Header from "./Header";
import Page2 from "./Page2";
import axios from 'axios';
import React1 from "react";

const baseURL = "http://localhost:5000/inputs/";

function App() {
  const [finalInput, setFinalInput] = useState([]);
  const [finalInputImage, setFinalInputImage] = useState([]);

  React1.useEffect(() => {
    axios.get(baseURL)
    .then((response) => {
      if(response.data.length > 0){
      setFinalInput(response.data);
      }
    });
  }, []);

  function addItem(input) {
    setFinalInput((prevValue) => {
      return [...prevValue, input];
    });
    // const formData = new FormData();
    // formData.append("title", finalInput.title);
    // formData.append("content", finalInput.content);
    // formData.append("imageFile", finalInput.imageFile);

    // axios.post('http://localhost:5000/inputs/add', formData)
    //   .then(res => setFinalInput(res.data))
    //   .catch((error) => console.log(error));
  }

  function addItem2(imageFile){
    setFinalInputImage(imageFile);
  }

  return (
    <div>
      <Header />
      <Brochure onAdd={addItem} onAdd2={addItem2} />
      {finalInput.map((eachInput, index) => {
        return <Page2 title={eachInput.title} content={eachInput.content} />;
      })}
      {finalInputImage.map((eachInput, index) => {
        return <Page2 img={`uploads/${eachInput.imageFile}`} />
      })}
    </div>
  );
}

export default App;
